drop database team;
create database team;

use team;

create table usuario(
	id_usuario int not null primary key auto_increment,
    nombre varchar(200) not null,
    tipo_documento varchar(40) ,
    documento numeric(11) unique not null,
    cod_empresa numeric(15) unique,
    contrasena varchar(25)
    
)ENGINE=INNODB;

create table producto(
	id_producto int not null primary key auto_increment,
    producto varchar(100) not null
)ENGINE=INNODB;

create table pedido(
	id_pedido int not null primary key auto_increment,
    cantidad int not null,
    fecha date not null,
    id_usuario int not null,
    id_producto int not null,
    
    constraint usuario_fk foreign key (id_usuario) references usuario(id_usuario),
    constraint producto_fk foreign key(id_producto) references producto(id_producto)
    
)ENGINE=INNODB;


select u.nombre, u.documento, p.producto, pe.cantidad, pe.fecha
from usuario u inner join pedido pe on pe.id_usuario = u.id_usuario
inner join producto p on pe.id_producto = p.id_producto;


